import React, { Component } from 'react'
// import { withRouter } from 'react-router-dom'
import Button from '@material-ui/core/Button';


class SignIn extends Component {

    state = {
        username: '',
        password: ''
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault()
        const {username, password} = this.state
        const loginInfo = {username: username, password: password}
        fetch('/login', {
            method: 'POST',
            headers: {"Content-type":"application/json"},
            body: JSON.stringify(loginInfo)
        })
        .then(res => res.json())
        .then(userInfo => {this.props.login(userInfo)
            this.setState({
                username: '',
                password: ''
            })
        })
    }    

    render() {
        // console.log(this.state)
        // const classes = this.useStyles();

        return (
            <div style={{contentAlign: 'center'}}>
                <h1 style={{fontFamily: 'Lucida Std'}}>Commute Better</h1>
                <form onSubmit={this.handleSubmit} style={{display:'block'}}>
                <input style={{border: '1px solidblack'}} type ='text' name='username' id='username' value={this.state.username} onChange={this.handleChange} placeholder='Email account'/><br/>
                <input style={{color:'black'}} type ='password' name='password' id='password' value={this.state.password} onChange={this.handleChange} placeholder='Password'/><br/>
                <Button type='submit'style={{color:'black'}}>Login</Button>
                </form>
            </div>
        )
    }
}
export default SignIn
