import React, { Component } from 'react'
import Button from '@material-ui/core/Button'
import MenuItem from '@material-ui/core/MenuItem';
import MenuList from '@material-ui/core/MenuList';
import Paper from '@material-ui/core/Paper';
import Dot from './icon/dot.png'
import Map from './icon/map.png'
import Circle from './icon/circle.png'
import Profile from './icon/profile.png'
import ClickAwayListener from '@material-ui/core/ClickAwayListener';
import './index.css';

export default class AddressInput extends Component {
    state={
        startingPoint: '',
        destination: '',
        profileMenu: ''
    }

    // handleClose = (event) => {
    //     if (anchorRef.current && anchorRef.current.contains(event.target)) {
    //       return;
    //     }

    //     setOpen(false);
    //   };

    // handleListKeyDown(event) {
    //     if (event.key === 'Tab') {
    //       event.preventDefault();
    //       setOpen(false);
    //     }
    //   }
    handleClick = (menu) => {
        this.setState(()=>{
            return {profileMenu: menu}
        })
    }

    render() {
        return (
                <div id="header" style={{width: '100%', height: '170px', boxShadow: "10px 5px 5px rgba(0, 0, 0, 0.1)"}}>
                    <h2 style={{fontFamily: 'Lucida Std', display: 'inline-block'}}>Commute Better</h2>
                    <div className = "dropdown">
                        <button className= "profilebtn"style={{border: 'none', background: 'none'}}><img className= "profileimg" style={{width:'35px',alignItems: 'center',marginLeft: '1300px', marginRight: '12px', display: 'inline-block'}} src={Profile} alt="Profile"/>
                            <div className = "dropdown-content">
                                <a href = "#">My Article</a>
                                <a href = "#">Log Out</a>
                            </div>
                        </button>
                    </div>



                    <form onSubmit={this.handleSubmit} style={{display:'block'}}>
                        <div>
                            <label style={{color: '#00d563', textAlign: 'center', display: 'inline-block'}}><div>Enter your location</div><div><img style={{width:'15px', marginRight: '12px'}} src={Circle} alt="Circle"/><input style={{padding: '0px 10px',border: '1.5px solid #00d563', color: '#8e8e8e', borderRadius: '4px', width:'300px', height: '34px', marginBottom: '20px', marginRight: '30px'}} type ='text' name='startingPoint' id='startingPoint' value={this.state.username} onChange={this.handleChange} placeholder='Starting point'/></div></label>

                            <label 
                            style={{display: 'inline-block', color: '#00d563', textAlign: 'center'}}><div>Enter destination</div>
                            <div><img style={{width:'45px', marginRight: '25px'}} src={Dot} alt="Dot"/><img style={{width:'20px',alignItems: 'center', marginRight: '12px'}} src={Map} alt="Map"/><input style={{padding: '0px 10px',border: '1.5px solid #00d563', color: '#8e8e8e', borderRadius: '4px', width:'300px',height: '34px', marginBottom: '20px'}} type ='text' name='destination' id='destination' value={this.state.username} onChange={this.handleChange} placeholder='Destination'/></div>
                            </label>

                            <Button type='submit'style={{color:'white', backgroundColor: '#1a1617', fontFamily: 'Lucida Std', textTransform: 'none', width: '100px', marginLeft: '20px'}}>Save</Button>
                        </div>
                    </form>
                </div>

        )
    }
}
